head.script:
scwin.onpageload = function() {

};

scwin.trigger1_onclick = function(e) {
    var opt = {
        "id":"data1",
        "type":"dataList",
        "option":{
            "baseNode":"list",
            "repeatNode":"map"
        },
        "columnInfo":[
            {
                "id":"JSON_Code",
                "name":"JSON_Code",
                "dataType":"text"
            },
            {
                "id":"JSON_CodeName",
                "name":"JSON_CodeName",
                "dataType":"text"
            }
        ]
    };
    $p.data.create(opt);
};

scwin.trigger2_onclick = function(e) {
    var arr1 = [];
    for(var i=0; i<5; i++){
        for(var j=0; j<4; j++){
            arr1[i*4+j] = "JSON_" + i+j;
        }
    }
    data1.setData(arr1);
};

scwin.trigger3_onclick = function(e) {
    var scId = "";
    var opt = {
        common:{
            fileNmae:"a.xlsx",
            multipleSheet:"true",
            showProcess:false
        },
        excelInfo:[
            {
                dataListId:"data1",
                sheetName:"JSON",
                removeColumn:"",
                wframeId:scId
            }
        ]
    };
    WebSquare.util.multipleDataListDownload(opt);
};
