head.script:
scwin.onpageload = function() {

};

scwin.trigger1_onclick = function(e) {
    requires("uiplugin.popup");
    var option = {
        id:"popup1",
        tpye:"wframePopup",
        height:"300px",
        width:"300px",
        closeAction:"scwin.closeAction"
    };
    $p.openPopup("", option);
};

scwin.closeAction = function(id,info){
    if(!info.isCloseButtonClick){
        return true;
    }
    alert("클로즈엑션 실행");
    return true;
}
