head.script:
scwin.onpageload = function() {
	wg1.addWidgets({
        id : "w1",
        title: "위젯1",
        src: "GridView1.xml",
        scope: "true",
        x: 0,
        y: 0,
        unitHeight : 2,
        unitWidth : 1,
        hasTitleBar : true
    });
    wg1.addWidgets({
        id : "w2",
        title: "위젯2",
        src: "pageList1.xml",
        scope: "true",
        x: 0,
        y: 0,
        unitHeight : 1,
        unitWidth : 1,
        hasTitleBar : false
    });
    wg1.getWidgetById("w2").showTitle(true);
    wg1.enableWidgetTitle(false);
};
